import { useState, } from "react";
import { TextInput } from "react-native";

export default function OctaveTuning({ socket, octave }) {
    const [upperValues, setUpperValues] = useState([])
    const [lowerValues, setLowerValues] = useState([])

    socket.emit("get_tuning_values", (upper, lower) => {
        setUpperValues(upper);
        setLowerValues(lower);
    });

    const upperInputs = upperValues.map((val, i) => {
        return <TextInput
            onChangeText={(e) => newTuning("u", i, e)}
            value={upperValues[i]}
            key = {"u"+i}
        />
    })

    const lowerInputs = lowerValues.map((val, i) => {
        return <TextInput
            onChangeText={(e) => newTuning("l", i, e)}
            value={lowerValues[i]}
            key = {"l"+i}
        />
    })

    function newTuning(ul, i, val) {
        if (ul === "u") {
            setUpperValues(upperValues.map((value, index) => {
                if (index === i) return val
                return value
            }))
        } else {
            setLowerValues(lowerValues.map((value, index) => {
                if (index === i) return val
                return value
            }))
        }
    }

    return (
        <div className="octave-tuning" >
            <>{upperInputs}</>
            <>{lowerInputs}</>
        </div>
    );
}
